// Function to beautify JSON
function beautifyJson(jsonString) {
  try {
    // Parse the JSON string
    const jsonObject = JSON.parse(jsonString);

    // Format the JSON with indentation
    return jsonObject; // Return the parsed object directly
  } catch (error) {
    // Return the error message if JSON is invalid
    return `Invalid JSON: ${error.message}`;
  }
}

// Retrieve the JSON object from the input data
const input = $input.all()[0].json;

// Extract the JSON string from the stdout field
const jsonString = input.stdout.match(/\[{.*}\]/s);

// Check if a valid JSON string is found
if (!jsonString) {
  return {
    json: {
      error: "No valid JSON string found in stdout."
    }
  };
}

// Beautify the JSON
const beautifiedJson = beautifyJson(jsonString[0]);

// Output the beautified JSON, ensuring each entry is in an object with a 'json' key
const output = beautifiedJson.map(entry => ({ json: entry }));

// Return the output
return output;

